const getCarouselSlideOrder = (index, current) => {
    if (index === current) {
        return 0;
    } else if (index === current - 1 || (current === 0 && index === 2)) {
        return 1;
    } else if (index === current + 1 || (current === 2 && index === 0)) {
        return -1;
    } else {
        return 2;
    }
}
const CarouselSlide = ({image, index, current}) => {
    return (
        <div className={index === current ? "carousel-slide active" : "carousel-slide"}>
            <img src={image} alt={"carousel"}/>
        </div>
    );
}

export default CarouselSlide;
