import {useEffect, useState} from "react";

const Toast = ({ message, time }) => {
  const [show, setShow] = useState(true);

  useEffect(() => {
    setTimeout(() => {
      setShow(false);
    }, time);
  }, [time]);

  return (
    <div className={`toast ${show ? "show" : ""}`}>
      <div className="toast__message">{message}</div>
    </div>
  );
}

export default Toast
