import "../../styles/molcules/ProductInformationColumn.css";
import { comma } from "../../utils/convert";
import Photo from "../atoms/Photo";
import Card from "../atoms/Card";
import React, { useState, useEffect} from "react";
import axios from "axios";
import ProductCard from "./ProductCard";

const ProductInformationColumn = ({ product }) => {
    const { productName, price, image } = product;
    // 
    const [products, setData] = useState(null);
    // const [loding, setLoding] = useState(true);

    useEffect(() => {
      const fetchData = async () => {
        try {
          const response = await axios.get('http://kakao-app-env.eba-kfsgeb74.ap-northeast-2.elasticbeanstalk.com/products');
          const jsonData = await response.data.response;

          setData(jsonData);
          // setLoding(false);
        } catch (error) {
          console.log(`Error fetching data:`, error);
        }
      };
      fetchData();
    }, []);
    // 
    return (
        <div className="product-information-column">
            {
                products? products.map((product) => (
                    <ProductCard key={product.id} product={product} />
                )): null
            }
            <Card to={`/product/${product.id}`}>
                <div className="product-photo">
            <div className="col">
                <Photo src={process.env.REACT_APP_API_URL+ product.image} alt={product.productName} />
            </div>
            <div className="col">
                <h1 className="product-name">{product.productName}</h1>
                <p className="product-price">{comma(product.price)}Ïõê</p>
            </div>
            </div>
            </Card>
        </div>
    );
};

export default ProductInformationColumn;
