    console.log(options);
    return (
        <ol className="option-list">
            {options.map((option, index) => (
                <li key={option.id} className="option" onClick={(option)}>
                    <span className="name">
                        {index + 1}. {option.optionName}
                    </span>
                    <span className="price">{comma(option.price)}Ïõê</span>
                </li>
            ))}
        </ol>
    );
};

export default OptionList;
