import Container from "../atoms/Container";
import ProductInformationColumn from "../molecules/ProductInformationColumn";
import OptionColumn from "../molecules/OptionColumn";
import React, { useState, useEffect} from 'react';
import axios from 'axios';

const ProductDetailTemplate = ({ product }) => {
    // 
    const [products, setData] = useState(null);
    // const [loding, setLoding] = useState(true);

    useEffect(() => {
      const fetchData = async () => {
        try {
          const response = await axios.get('http://kakao-app-env.eba-kfsgeb74.ap-northeast-2.elasticbeanstalk.com/products');
          const jsonData = await response.data.response;

          setData(jsonData);
          // setLoding(false);
        } catch (error) {
          console.log(`Error fetching data:`, error);
        }
      };
      fetchData();
    }, []);
    // 
    return (
        <Container>
            <ProductInformationColumn product={product}/>
            <OptionColumn product={product} />
            {/*  */}
            <ProductInformationColumn product />
            <OptionColumn product />

            {
            products? products.map((product) => (
            <ProductInformationColumn key={product.id} product={product} />
      )): null}

            { 
            products? products.map((product) => (
            <OptionColumn key={product.id} product={product} />
      )): null}
        </Container>
    )

}

export default ProductDetailTemplate;
