import { useParams } from "react-router-dom";
import Loader from "../component/atoms/Loader";
import { getProductById } from "../services/product";
import Container from "../component/atoms/Container";
import { useQuery } from "react-query";
import ProductDetailTemplate from "../../src/component/templates/ProductDetailTemplate";
import React, { useState, useEffect} from 'react';
import axios from 'axios';

const ProductDetailPage = () => {
    const { id } = useParams();
    const { data, error, isLoading } = useQuery(`product/${id}`, () =>
        getProductById(id)
    );

    const product = data?.data?.response;
    console.log(product);

    const [products, setData] = useState(null);
    // const [loding, setLoding] = useState(true);

    useEffect(() => {
      const fetchData = async () => {
        try {
          const response = await axios.get('http://kakao-app-env.eba-kfsgeb74.ap-northeast-2.elasticbeanstalk.com/products');
          const jsonData = await response.data.response;

          setData(jsonData);
          // setLoding(false);
        } catch (error) {
          console.log(`Error fetching data:`, error);
        }
      };
      fetchData();
    }, []);



    return (
        <div>
            {isLoading && <Loader />}
            {error && <div>{error.message}</div>}
            {product && <ProductDetailTemplate product={product} />}
            {/* <ProductDetailTemplate product /> */}




        </div>
    );
};

export default ProductDetailPage;
