import React, { useState } from "react";
import "./App.css";
// import Toggle from "./components/togglebutton/toggle";
// import Toast from "./components/toast/toast";
import Radio from "./components/radiobutton/radio";

function App() {
  const [theme, setTheme] = useState({ dark: false, light: false });

  const onChangeTheme = (e) => {
    const { name } = e.target;
    if (name === "light") {
      setTheme({ dark: false, light: true });
    }
    if (name === "dark") {
      setTheme({ dark: true, light: false });
    }
  };

  return (
    <div className="select-theme">
      <p>Please select a theme</p>
      <Radio text="Dark" onChange={onChangeTheme} checked={theme.dark} />
      <Radio text="Light" onChange={onChangeTheme} checked={theme.light} />
    </div>
  );
}

export default App;
