import React from "react";
import "./radio.css";

const Radio = () => {
  const onChange = (e) => {
    const { name } = e.target;
    console.log("clicked", name);
  };

  return (
    <label htmlFor="dark" className="radio-label">
      <input
        className="radio-input"
        type="radio"
        onChange={onChange}
        checked={false}
      />
      Dark
    </label>
  );
};

export default Radio;
