import React, { useState, useEffect } from "react";
import "./toast.css";

const Toast = ({ toastlist }) => {
  return (
    <div>
      {toastlist.map((toast, i) => (
        <div key={i} style={{ backgroundColor: toast.backgroundColor }}>
          <button>X</button>
          <div>
            <p>{toast.title}</p>
            <p>{toast.description}</p>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Toast;
