import React from "react";
import "./App.css";
// import Toggle from "./components/togglebutton/toggle";
// import Toast from "./components/toast/toast";
import Radio from "./components/radiobutton/radio";

function App() {
  function Radio() {
    const onChange = (e) => {
      const { name } = e.target;
      console.log("clicked", name);
    };

    return (
      <label htmlFor="dark">
        <input
          type="radio"
          name="dark"
          id="dark"
          value="dark"
          onChange={onChange}
          checked={true}
        />
        Dark
      </label>
    );
  }
}

export default App;
