import React, { useState } from "react";
import { ChevronLeft, ChevronRight } from "react-feather";
import "./carousel.css";

const Carousel = ({ data }) => {
  return (
    <div className="viewport">
      {data.map((item, idx) => {
        return <img src={item.src} alt={item.alt} key={idx} />;
      })}
    </div>
  );
};

export default Carousel;
