import React from "react";
import "./breadcrumb.css";

const Bread = (props) => {
  return (
    <nav>
      <ol>
        {props.crumbs.map((crumb, ci) => {
          const disabled = isLast(ci) ? "disabled" : "";
          return (
            <li key={ci}>
              <button onClick={() => props.selected(crumb)}>{crumb}</button>
            </li>
          );
        })}
      </ol>
    </nav>
  );
};

export default Bread;
