import React from "react";
import "./radio.css";

const Radio = ({ name, id, value, onChange, checked, text }) => {
  return (
    <label htmlFor={id} className="radio-label">
      <input
        className="radio-input"
        type="radio"
        name={name}
        id={id}
        value={value}
        onChange={onChange}
        checked={checked}
      />
      <span className="custom-radio" />
      {text}
    </label>
  );
};

const App = () => {
  const [theme, setTheme] = React.useState({ dark: false, light: false });

  const onChangeTheme = (e) => {
    const { name } = e.target;
    if (name === "light") {
      setTheme({ dark: false, light: true });
    }
    if (name === "dark") {
      setTheme({ dark: true, light: false });
    }
  };

  return (
    <div className="select-theme">
      <p>Please select a theme</p>
      <RadioButton
        name="dark"
        id="dark"
        value="Dark"
        text="Dark"
        onChange={onChangeTheme}
        checked={theme.dark}
      />
      <RadioButton
        name="light"
        id="light"
        value="Light"
        text="Light"
        onChange={onChangeTheme}
        checked={theme.light}
      />
    </div>
  );
};

export default App;
