import React from "react";
import "./radio.css";

const Radio = (id, name, checked, text) => {
  return (
    <label htmlFor={id} className="radio-label">
      <input
        className="radio-input"
        type="radio"
        name={name}
        checked={checked}
      />
      <span className="custom-radio" />
      {text}
    </label>
  );
};

export default Radio;
