import { useState, React } from "react";
import styles from "../css/Checklist.module.css";


function Checked() {

}
const Checklist = () => {
    const checkBoxList = ["빨강", "노랑", "파랑", "검정", "회색"];
    const [list, setList] = useState([]);
    const handleCheckboxChange = (event) => {
        const { value, checked } = event.target;
        if (checked) {
            setList([...list, value]);
        } else {
            setList(list.filter(item => item !== value));
        }
    };
    return (
        <div>
            <h2 className={styles.h2}>4. 체크리스트 구현</h2>
            {checkBoxList.map((item) => (
                <div>
                    <input type="checkbox" id={item} onChange={handleCheckboxChange}
                        checked={list.includes(item)}></input>
                    <label htmlFor={item}>{item}</label>
                </div>

            )

            )}
